primarySearchTemplate.template:
{
    "query": {
        "bool": {
            "must": [
                {{#if query}}
                    {
                        "query_string": {
                            "query": "{{query}}", 
                            "default_operator": "and", 
                            "default_field": "text_bag"
                        }
                    },
                {{/if}}
                {{#if roles}}
                    { "terms": { "roles_slugs": ["{{roles}}"] } },
                {{/if}}
                {{#if sectors}}
                    { "terms": { "sectors_slugs": ["{{sectors}}"] } },
                {{/if}}
                {{#if regions}}
                    { "terms": { "region_slug": ["{{regions}}"] } },
                {{/if}}
                {{#if statuses}}
                    { "terms": { "status_slug": ["{{statuses}}"] } },
                {{/if}}
                {{#if minimumSalary}}
                    { "range": { "salary_min": { "gte": {{minimumSalary}} } } },
                {{/if}}
                { "terms": { "country_iso3": ["GBR"] } },
                { "terms": { "vacancy_status": ["vacancy-open"] } }
            ]
        }
    },
    "sort": [{
        "Id": {
            "order": "desc"
        }
    }]
}
