                                                        &pound;{{addCommas salary_min}} - &pound;{{addCommas salary_max}}
                                                    {{else}}
                                                        &pound;{{addCommas salary_min}}
                                                    {{/gt}}
                                                {{/and}}
                                            {{/and}}
                                        {{/and}}
                                        {{#eq status "Part time"}} pro-rata {{/eq}}
                                    {{else}}
                                        {{#if remuneration_type}} {{remuneration_type}} {{/if}} 
                                    {{/eq}}
                                </span>
                            </span>
                        </li>

                        <li class="mdc-list-item">
                            <span class="mdc-list-item__text">
                                Salary info:
                                <span class="mdc-list-item__text__secondary">
                                    {{salary_info}}
                                </span>
                            </span>
                        </li>

                        <li class="mdc-list-item">
                            <span class="mdc-list-item__text">
                                Closing date:
                                <span class="mdc-list-item__text__secondary">
                                    {{moment closing_date "D MMMM YYYY"}}
                                </span>
                            </span>
                        </li>

                        <li class="mdc-list-item">
                            <span class="mdc-list-item__text">
                                Location:
                                <span class="mdc-list-item__text__secondary">
                                    {{location}}
                                </span>
                            </span>
                        </li>
                    </ul>
                </section>
                <section class="mdc-card__supporting-text">
                        {{ellipsis description 500}}
                </section>
                <section class="mdc-card__actions action-buttons-right">
                    <a href="/vacancy/{{slug}}" class="mdc-button mdc-button--compact mdc-card__action">
                        View full details
                    </a>            
                </section>
            </div>            
        </div>
    {{/forEach}}
{{else}}
    <div class="container-fluid">
        No results
    </div>
{{/and}}

