<meta property="og:summary" content="{{description}}" data-dynamic="true">
<meta name="description" content="{{description}}" data-dynamic="true">
{{/if ~}}
{{#if rendered.json_ld ~}}
<script type="application/ld+json" data-dynamic="true">
{{{rendered.json_ld}}}
</script>
{{/if ~}}
{{/with ~}}
{{else}}
<title>{{route.metaData.title}}</title>
<meta property="og:title" content="{{route.metaData.title}}" data-dynamic="true">
{{/if ~}}
{{/compare ~}}

