<meta property="og:image:height" content="96" data-dynamic="true">
{{#ifAny og_summary description ../og_summary ../description ~}}
<meta property="og:summary" content="{{{default og_summary (default ../og_summary (default description ../description))}}}" data-dynamic="true">
<meta name="description" content="{{{default description (default ../description (default og_summary ../og_summary))}}}" data-dynamic="true">
{{/ifAny ~}}
{{#if rendered.json_ld ~}}
<script type="application/ld+json" data-dynamic="true">
{{{rendered.json_ld}}}
</script>
{{/if ~}}
{{/with ~}}
{{else}}
<title>SCVO – Scottish Council for Voluntary Organisations</title>
<meta property="og:title" content="SCVO – Scottish Council for Voluntary Organisations" data-dynamic="true">
{{/if ~}}
