  <li><a href="/">Home</a></li>
  {{#each breadcrumb ~}}
  <li><a href="{{replace (replace url 'http://cms.scvo.org' '') 'https://cms.scvo.org' ''}}">{{{striptags title}}}</a></li>
  {{/each ~}}
  <li>{{{striptags title}}}</li>
</ul>
{{/with ~}}

partials.related_events:
{{#compare data.supplimentaryResponses.related_events.hits.total ">" 0 ~}}
<h2 class="author">Similar events</h2>
<div class="mdc-layout-grid related">
  <div class="mdc-layout-grid__inner">
    {{#each data.supplimentaryResponses.related_events.hits.hits ~}}
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-6-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
      <ul class="supplimentary-list">
        {{{_source.rendered.scvo_list}}}
      </ul>
    </div>
    {{/each ~}}
  </div>
</div>
{{/compare ~}}

partials.related_posts:
{{#compare data.supplimentaryResponses.related_posts.hits.total ">" 0 ~}}
<div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-12 related page-related">
  <h3 class="blogs">Related posts</h3>
  <div class="mdc-layout-grid">
    <div class="mdc-layout-grid__inner">
      {{#each data.supplimentaryResponses.related_posts.hits.hits ~}}
      <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-3-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
        {{{_source.rendered.scvo_gist}}}
      </div>
      {{/each ~}}
    </div>
  </div>
</div>
{{/compare ~}}

partials.sidebar_menu:
{{#*inline "childMenu"}}
{{#each items}}
<a class="menu-level-{{../level}} mdc-button mdc-button--raised{{#if (regexMatch ../path route "gi")}} mdc-button--active{{/if}}" href="{{path}}">{{{label}}}</a>
{{#if children}}
{{> childMenu items=children path=../path level=(add ../level 1)}}
{{/if}}
{{/each}}
{{/inline}}

<nav class="menu-sidebar" id="{{id}}">
  {{> childMenu items=items path=path level=1}}
</nav>

